import React, {useState, useEffect } from 'react';
import queryString from 'query-string'; // this is a library that allows us to get data using query string from the URL
import io from 'socket.io-client'

let socket;

import './Chat.css';

const Chat = () => {
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const ENDPOINT = 'localhost:5000';
    useEffect(() => {
        const { name, room } = queryString.parse(location.search);

        socket = io(ENDPOINT);

        setName(name);
        setRoom(room);

    });

    return (
        <h1>Chat</h1>
    )
}


export default Chat;
