const Chat = () => {
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const [message, setMessesge] = useState('');
    const [messages, setMesseges] = useState([]);
    const ENDPOINT = 'localhost:5000';
    const [searchParams] = useSearchParams();

    useEffect(() => {

